import React, { useEffect } from 'react'
import styled from 'styled-components'

const Button = ({ keyCode, sample, id, url, setDisplay, audioContextState, setCurrentSample, audioRef }) => {
    // console.log('id: ', id);
    // console.log('keyCode: ', keyCode);

    // const drumSampleAudio = new Audio(`${process.env.REACT_APP_PATH_TO_FILES}${url}`)

    //KEYBOARD EVENT LISTENER





    useEffect(() => {

        window.addEventListener('keydown', handleKeyDown);

        return () => {
            window.removeEventListener('keydown', handleKeyDown);
        };


    }, [handleKeyDown]);

    return (

        <Wrapper>

            <Label>{sample.toUpperCase()}</Label>

            <Toggle

                // onKeyDown={handleKeyPress}

                onClick={() => {

                    if (audioContextState?.state === 'suspended') {
                        audioContextState.resume();
                        if (audioRef.current) {
                            audioRef.current.play()
                        }
                    }

                    if (audioContextState?.state === 'running') {
                        if (audioRef.current) {
                            audioRef.current.play()
                        }
                    }

                    setDisplay(sample)
                    setCurrentSample(`${process.env.REACT_APP_PATH_TO_FILES}${url}`)

                }}

            />

        </Wrapper>

    )
}

export default Button

const Wrapper = styled.div`
`
const Label = styled.p`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
font-family: helvetica;
font-size: 10px;
background-color: #c6c4bf;
margin: 0 4px 0 4px;

`
const Toggle = styled.button`
margin: 12px;
background-color: #8a8886;
height: 40px;
width: 60px;
border-radius: 4px;
`
