import React from 'react'
import styled from 'styled-components'

const Button = ({ keyCode, sample, id, url, setDisplay, audioContextState, setCurrentSample, audioRef }) => {
    // console.log('audioRef.current: ', audioRef.current);
    // console.log('audioContextState: ', audioContextState);
    console.log('keyCode: ', keyCode);

    // const drumSampleAudio = new Audio(`${process.env.REACT_APP_PATH_TO_FILES}${url}`)

    const handleClick = (e) => {

        if (audioContextState?.state === 'suspended') {
            audioContextState.resume();
            if (audioRef.current) {
                audioRef.current.play()
            }
        }

        if (audioContextState?.state === 'running') {
            if (audioRef.current) {
                audioRef.current.play()
            }
        }

        setDisplay(sample)
        setCurrentSample(`${process.env.REACT_APP_PATH_TO_FILES}${url}`)

    }

    return (

        <Wrapper>

            <Label>{sample.toUpperCase()}</Label>

            <Toggle

                onKeyPress={(e) => {
                    if (e.key === keyCode) {
                        handleClick()
                    }
                }}

                onClick={handleClick}

            />

        </Wrapper>

    )
}

export default Button

const Wrapper = styled.div`
`
const Label = styled.p`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
font-family: helvetica;
font-size: 10px;
background-color: #c6c4bf;
margin: 0 4px 0 4px;

`
const Toggle = styled.button`
margin: 12px;
background-color: #8a8886;
height: 40px;
width: 60px;
border-radius: 4px;
`
