// 'use strict';
const express = require('express');
const morgan = require('morgan');
const app = express();

app.use(morgan('tiny'))
app.use(express.static('public'))

app.use(express.json())

const PORT = process.env.PORT || 8000;

const {
    loginAuthentication,
    getUsers,
    addUser,
    savePatch
} = require("./handlers");

app.get('/api/getUsers', getUsers)

app.post('/api/login', loginAuthentication)
app.post("/api/addUser", addUser)
app.post("/api/savePatch", savePatch)

app.get('*', (req, res) => {
    res.status(404).json({ status: 404, message: 'This is obviously not the page you are looking for.'});
})

app.listen(8000, () => console.log(`Listening on port 8000`));
