import React, { useState } from 'react'
import { useHistory } from 'react-router-dom'
import styled from "styled-components";

const Login = () => {

    const [loginEmail, setLoginEmail] = useState('')
    const [loginPassword, setLoginPassword] = useState('')

    const history = useHistory()

    const submitCustomerData = async (ev) => {

        ev.preventDefault();

        const loginRequestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                loginEmail: loginEmail,
                loginPassword: loginPassword
            })
        };

        fetch("/api/login", loginRequestOptions).then((res) => {
            return res.json()
        }).then(data => {
            if (data.data?.validatedPassword === true) {
                localStorage.clear();
                localStorage.setItem('currentUser', JSON.stringify(loginEmail));
                history.push(`/kit`)
            } else {
                window.alert(`Invalid login`)
            }
        });



    }

    return (

        <Wrapper>
            <div>Login</div>
            <LoginInformation>
                <Email
                    type="text"
                    placeholder="E-mail"
                    onChange={(ev) => setLoginEmail(ev.target.value)}
                />
                <Password
                    type="password"
                    placeholder="Password"
                    onChange={(ev) => setLoginPassword(ev.target.value)}
                />
                <LoginButton
                    type="submit"
                    onClick={submitCustomerData}>
                    Lets go!
                </LoginButton>
            </LoginInformation>
        </Wrapper>

    )
}

export default Login

const Wrapper = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
height: 87vh;
border: 3px solid red;
`

const LoginInformation = styled.form`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
margin: 5px;
width: 250px;
height
border: 3px solid orange;
`

const Email = styled.input`
margin: 5px;
width: 250px;
border: 3px solid green;
text-align: center;
`

const Password = styled.input`
margin: 5px;
width: 250px;
text-align: center;
border: 3px solid blue;
`

const LoginButton = styled.button`
margin: 5px;
width: 50px;
text-align: center;
border: 3px solid violet;
`
