import React from 'react'
import styled from 'styled-components'

const Button = ({ keyCode, sample, id, url, setDisplay, setCurrentSample, audioRef }) => {

    // const drumSampleAudio = new Audio(`${process.env.REACT_APP_PATH_TO_FILES}${url}`)
    <audio ref={audioRef} onEnded={() => setIsPlaying(true)} src={"/TR-505Kick.wav"} controls autoPlay />

    return (

        <Wrapper>

            <Label>{sample.toUpperCase()}</Label>

            <Toggle onClick={() => {
                // actx.resume()
                audioRef.current.play()
                setDisplay(sample)
                setCurrentSample(audioRef)
            }} />

        </Wrapper>

    )
}

export default Button

const Wrapper = styled.div`
`
const Label = styled.p`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
font-family: helvetica;
font-size: 10px;
background-color: #c6c4bf;
margin: 0 4px 0 4px;

`
const Toggle = styled.button`
margin: 12px;
background-color: #8a8886;
height: 40px;
width: 60px;
border-radius: 4px;
`
