import React, { useState } from 'react'
import { useHistory } from 'react-router-dom'
import styled from "styled-components";
import { passwordVerification } from './SignUpVerification';
const SignUp = () => {

    const [givenName, setGivenName] = useState('')
    const [surname, setSurname] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [userExists, setUserExists] = useState(false)
    const [completedForm, setCompletedForm] = useState(true)

    const history = useHistory()

    const submitFormData = (ev) => {

        passwordVerification(ev)

        ev.preventDefault()

        const addUserRequestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                givenName: givenName,
                surname: surname,
                email: email,
                password: password
            })
        };

        fetch("/api/addUser", addUserRequestOptions).then((res) => {
            return res.json()
        }).then(data => {
            if (data.error === "invalid-email") {
                setUserExists(true)
            }

            // for (let input in data) {
            //     if (data[input].trim().length < 1) {
            //         setCompletedForm(false)
            //     }        
            // }

            if (data.status === 200) {
                history.push(`/login`)
            }

            console.log(data)
        });

    }

    return (

        <Wrapper>
            <div>SignUp</div>

            <NewUserInformation>
                <GivenName
                    type="text"
                    placeholder="First Name"
                    onChange={(ev) => setGivenName(ev.target.value)}
                    required
                />
                <Surname
                    type="text"
                    placeholder="Last Name"
                    onChange={(ev) => setSurname(ev.target.value)}
                    required
                />
                <Email
                    type="email"
                    placeholder="E-mail"
                    onChange={(ev) => setEmail(ev.target.value)}
                    required
                />
                <Password
                    id="password"
                    type="password"
                    placeholder="Password"
                    onChange={(ev) => setPassword(ev.target.value)}
                    required
                />
                <ConfirmPassword
                    id="confirmPassword"
                    type="password"
                    placeholder="Confirm Password"
                    required
                />

                {userExists && <UserExists>Email already exists!</UserExists>}
                {/* {!completedForm && <FormIncomplete>Sign-up form incomplete!</FormIncomplete>} */}


                <Join
                    type="submit"
                    onClick={submitFormData}>
                    Join
                </Join>
            </NewUserInformation>
        </Wrapper>

    )

}

export default SignUp

const Wrapper = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
border: 3px solid red;
`
const NewUserInformation = styled.form`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
margin: 5px;
width: 160px;
border: 3px solid orange;
`

const GivenName = styled.input`
margin: 5px;
text-align: center;
border: 3px solid green;
`

const Surname = styled.input`
margin: 5px;
text-align: center;
border: 3px solid blue;
`

const Email = styled.input`
margin: 5px;
text-align: center;
border: 3px solid violet;
`

const Password = styled.input`
margin: 5px;
text-align: center;
border: 3px solid purple;
`

const ConfirmPassword = styled.input`
margin: 5px;
text-align: center;
border: 3px solid maroon;
`

const UserExists = styled.p`
`

const FormIncomplete = styled.p`
`

const Join = styled.button`
margin: 5px;
width: 150px;
text-align: center;
border: 3px solid yellow;
`
