import React, { useState } from 'react'
import styled from 'styled-components'

const Delay = ({ delayTime, delayTimeValue, delayFeedback, delayFeedbackGainState }) => {

    const [delayOnOff, setDelayOnOff] = useState("OFF")

    const delayOnOffClick = (ev) => {
        if (delayOnOff === "OFF") {
            setDelayOnOff("ON")
        }

        if (delayOnOff === "ON") {
            setDelayOnOff("OFF")
        }
    }

    return (

        <Wrapper>
            <Effect>Delay</Effect>
            <Toggle onClick={delayOnOffClick}>
                {delayOnOff}
            </Toggle>
            <DelayTime
                onChange={delayTime}
                type="range"
                value={delayTimeValue}
                min="0"
                max={(delayOnOff === "ON") ? "1" : "0"}
                step="0.01"
            />
            <Feedback
                onChange={delayFeedback}
                type="range"
                value={(delayOnOff === "ON") ? delayFeedbackGainState : "0.0"}
                min="0"
                max="1"
                step="0.001"
            />

        </Wrapper>

    )
}

export default Delay

const Wrapper = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
`

const Effect = styled.p`

`

const Toggle = styled.button`
width: 40px;
height: 40px;
margin: 5px;
`

const DelayTime = styled.input`
`

const Feedback = styled.input`
`
