import React from 'react'
import styled from 'styled-components'

const Button = ({ keyCode, sample, id, url, setDisplay, audioContextState, setCurrentSample, audioRef }) => {
    console.log('audioRef: ', audioRef.current);
    console.log('audioContextState: ', audioContextState);

    const drumSampleAudio = new Audio(`${process.env.REACT_APP_PATH_TO_FILES}${url}`)

    return (

        <Wrapper>

            <Label>{sample.toUpperCase()}</Label>

            <Toggle onClick={() => {
                                if (audioContextState?.state === 'suspended') {
                                    audioContextState.resume();
                                    // console.log("audioContextState.state AFTER :", audioContextState?.state)
                                    // console.log('audioRef.current: ONCLICK', audioRef.current);
                                    if (audioRef.current) {
                                        audioRef.current.play()
                                    }
                                }

                                if (audioContextState?.state === 'running') {
                                    if (audioRef.current) {
                                        audioRef.current.play()
                                    }
                                }
                // actx.resume()
                drumSampleAudio.play()
                setDisplay(sample)
                // setCurrentSample(drumSampleAudio)
            }} />

        </Wrapper>

    )
}

export default Button

const Wrapper = styled.div`
`
const Label = styled.p`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
font-family: helvetica;
font-size: 10px;
background-color: #c6c4bf;
margin: 0 4px 0 4px;

`
const Toggle = styled.button`
margin: 12px;
background-color: #8a8886;
height: 40px;
width: 60px;
border-radius: 4px;
`
