import React from 'react'
import { Link } from 'react-router-dom'
import styled from "styled-components";

const Header = () => {

    const loggedIn = JSON.parse(localStorage.getItem("currentUser"));

    const handleClick = () => {
        localStorage.clear();
    }

    return (
        <Wrapper>
            <Logo>Drummy</Logo>
            <Link to='/'
                onClick={handleClick}
                style={{textdecoration: "none"}}
            >
                <Logout>
                    {loggedIn ? "Log Out" : ""}
                </Logout>
            </Link>
        </Wrapper>

    )
}

export default Header

const Wrapper = styled.div`
display: flex;
justify-content: space-between;
align-items: center;
min-width: 900px;
height: 5vh;
border: 3px solid black;
`

const Logo = styled.p`
font-family: Roboto;
font-weight: bold;
margin-left: 15px;
`

const Logout = styled.p`
font-family: Roboto;
margin-left: 15px;
`
