export const passwordVerification = (event) => {
    passwordLength(event)
    passwordMatch(event)
}

const passwordLength = (event) => {

    const password = document.getElementById("password");

    if (password.value.length >= 10) {
        return true;
    } else {
        window.alert('Password must be at least 10 characters')
        return false;
    }

};

const passwordMatch = (event) => {

    const password = document.getElementById("password");
    const confirmPassword = document.getElementById("confirmPassword")

    if (password.value === confirmPassword.value) {
        return true;
    } else {
        window.alert('Passwords do not match')

        return false;
    }

}

const isDataComplete = (data) => {

    let completedData = true

    for (let field in data) {

        if (data[field].trim().length < 1) {
            completedData = false
        }

    }
    return completedData
}

const isValidEmail = (data) => {

    if (!data.email.includes('@')) {
        return false
    }
    return true
}

export const formVerification = (data) => {

    if (!isDataComplete(data)) return "Sign-up form incomplete!"

    if (!isValidEmail(data)) return "E-mail address is not valid!"

    return true

}
